type SVGIcon = React.FC<React.SVGProps<SVGSVGElement>>;


declare module "*.svg?react" {
  export const ReactComponent: SVGIcon;
  const src: string;
  export default src;
}

declare module "*.png" {
  const src: string;
  export default src;
}

declare module "*.jpg" {
  const src: string;
  export default src;
}

declare module "*.jpeg" {
  const src: string;
  export default src;
}

declare module "*.gif" {
  const src: string;
  export default src;
}